import React, { Component } from 'react';
import Swappable from './components/SwappableComponent'
import './App.css';
import DataTable from './components/tableWidget';
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";


const styles = theme => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing.unit * 2,
    textAlign: "center",
    color: theme.palette.text.secondary
  }
});

class App extends Component {
    constructor(props) {
      super(props);
      this.state={
        widgets:[
          {id:1, content: <DataTable/>},
          {id:2, content: "#2"},
          {id:3, content: "#3"},
          {id:4, content: "#4"}
        ]
      }
      this.delete=this.delete.bind(this);

    }  
    delete(id){
        this.setState({
          widgets:this.state.widgets.filter(el => el == id )
        }); 
    }
    render(){
        return(
          <div>
              <Layout widgets={this.state.widgets} delete={this.delete}/>
          </div>
        );
      }
}


class Layout extends React.Component{
    delete(id) {
        this.props.delete(id);
    }
    render() {
    const { classes } = this.props;

    return (
    <div className={classes.root}>
        <Grid container spacing={24}>
            {
                this.props.widgets.map(el=>(
                        <Grid item xs={12} sm={6}>
                            <Paper className={classes.paper}><Swappable id={el.id} content={el.content}/></Paper>
                            <button key={el} onClick={() => this.delete(el)}></button>
                        </Grid>
                    )
                )
            }
        </Grid>
    </div>
    );
}
  }

  App.propTypes = {
    classes: PropTypes.object.isRequired
  };

  export default withStyles(styles)(App);
