import React, { Component } from 'react';
import Swappable from './components/SwappableComponent'
import './App.css';
import DataTable from './components/tableWidget';
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";

class App extends Component{
    constructor(props){
        super(props);
        this.state={
            widgets:[
                {id:'1'},
                {id:'2'},
                {id:'3'},
                {id:'4'}
            ] 
        }
    this.delete=this.delete.bind(this);
    }
    delete(id){
        this.setState({
            widgets:this.state.widgets.filter(el=>el!=id)
        });
    }
    render(){
        return(
            <div>
              <ul>
                <Layout widgets={this.state.widgets} delete={this.delete}/>
              </ul>
            </div>
          );
    }
}

class Layout extends React.Component {
    delete(id) {
      this.props.delete(id);
    }

    render() {
      return (
        <div>
         <Grid container spacing={24}>
          {this.props.widgets.map(el => (
            <div>
                <span>{el.id}</span>
                <Grid item xs={12} sm={6}>
                    <Paper className={classes.paper}> <Swappable id='2' content="#2"/></Paper>
                </Grid>
                <button key={el} onClick={() => this.delete(el)}>
                {el.id}
                </button>
            </div>
          ))}
          </Grid>
        </div>
      );
    }
  }

const styles = theme => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing.unit * 2,
    textAlign: "center",
    color: theme.palette.text.secondary
  }
});


export default withStyles(styles)(App);
