import React, { Component } from 'react';
import Swappable from './components/Swappable/SwappableComponent'
import './App.scss';
import AddWidgetDialog from './components/Modal/Modal';
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Toggle from 'react-toggle'

import UserManagement from './components/UserManagement/UserManagementWidget'
import NewOrder from './components/NewOrder/NewOrder';
import OrderHistory from './components/OrderHistory/OrderHistoryWidget';
import Subscriptions from './components/Subscriptions/Subscriptions'

const styles = theme => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing.unit * 2,
    textAlign: "center",
    color: theme.palette.text.secondary
  }
});

class App extends Component {
    constructor(props) {
      super(props);
      this.state={
        selectedWidgetId: null,
        widgetOptions:[{name:"Order History", comp:<OrderHistory/>},{name:"Add To Cart", comp:<NewOrder/>},{name:"User Management", comp:<UserManagement/>},{name:"Subscriptions", comp:<Subscriptions/>}],

        widgets:[ //array for layout 
          //{id:1, content: <img className="img" src="https://www.grandandtoy.com/OnlineRepository/dcr/landingpage/Services/header-banner-images-services.png" alt="new"/>, xs:12, sm:12},
          {id:1, content: <NewOrder/>,xs:12,sm:6},
          {id:2, content: <OrderHistory/>,xs:12,sm:6},
          {id:3, content: <OrderHistory/>,xs:12,sm:6},
          {id:4, content: <UserManagement/>,xs:12,sm:6}
        ],
        editMode:false,
        isModalOpen: false,
        selectedWidgetIndex:null
      }

    }
    handleWidgetSelection=(id) => {

      this.setState({ selectedWidgetId: id })
      const copyWidgets=Object.assign([],this.state.widgets); 
      let widget = Object.assign({}, this.state.widgets[this.state.selectedWidgetIndex]);

      widget.content=this.state.widgetOptions.find(x=>x.name==id).comp;

      copyWidgets[this.state.selectedWidgetIndex]=widget; 
      this.setState({ 
          widgets:copyWidgets
      }) 
      this.onRequestClose();
    }
    deleteEvent=(index)=>{
        const copyWidgets=Object.assign([],this.state.widgets);
        let widget=this.state.widgets[index];
        widget.content="";
        copyWidgets[index]=widget;
        this.setState({
            widgets:copyWidgets
        })
    }
    addEvent=(index)=>{
        this.setState({ 
            isModalOpen: true,
            selectedWidgetIndex:index
        })

    }

    onRequestClose = () => {
        this.setState({
          isModalOpen: false,
        });
      }

    editEvent=()=>{
      this.setState(prevState=>({
        editMode:!prevState.editMode,
      }));
    }
    newWidget=()=>{
      this.setState({ myArray: this.state.widgets.push( {id:5, content: <OrderHistory/>,xs:12,sm:6}) })
    }
    render() {
      const { classes } = this.props;

      return (
      <div className="wrapper"> 
        <div className={classes.root}>
        <AddWidgetDialog handleWidgetSelection={this.handleWidgetSelection} widgets={this.state.widgetOptions} isModalOpen={this.state.isModalOpen} onRequestClose={this.onRequestClose} />
          <div className="gridItem">
          <Grid container spacing={24}>
          <div className="editToggle">
          <div className="checkbox">
          <label>

          <span className="edit">Edit</span>
              <Toggle
                onClick={this.editEvent}
                defaultChecked={this.state.tofuIsReady}
                icons={false}
                onChange={this.handleTofuChange} />

          </label>
          </div>
          </div>
            <Grid item xs={12}>
                <Paper className={classes.paper}><img className="img" src="https://www.grandandtoy.com/OnlineRepository/dcr/landingpage/Services/header-banner-images-services.png" alt="new"/></Paper>
            </Grid>
              {
                  this.state.widgets.map((widget,index)=>{
                      return(
                          <Grid item xs={widget.xs} sm={widget.sm}>
                              <Paper className={classes.paper}><Swappable id={widget.id} content={widget.content} delete={this.deleteEvent.bind(this,index)} add={this.addEvent.bind(this,index)} editMode={this.state.editMode}/></Paper>
                          </Grid>
                      )
                  })
              }
          </Grid>
          </div>
        </div>
      </div>
      );
    }
  }

  App.propTypes = {
    classes: PropTypes.object.isRequired
  };

  export default withStyles(styles)(App);
