import React, { Component } from 'react';
import Swappable from './components/Swappable/SwappableComponent'
import './App.scss';
import DataTable from './components/TableWidget/tableWidget';
import CheckboxList from './components/CheckboxList';
import AddWidgetDialog from './components/Modal/Modal';
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import { Table } from '@material-ui/core';



const styles = theme => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing.unit * 2,
    textAlign: "center",
    color: theme.palette.text.secondary
  }
});

class App extends Component {
    constructor(props) {
      super(props);
      this.state={
        selectedWidgetId: null,
        widgetOptions:[{name:"Data Table", comp:<DataTable/>},{name:"List", comp:<CheckboxList/>}],

        widgets:[ //array for layout 
          {id:1, content: "", xs:12},
          {id:2, content: <CheckboxList/>,xs:12,sm:6},
          {id:3, content: <DataTable/>,xs:12,sm:6},
          {id:4, content: "",xs:12,sm:6},
          {id:5, content: "",xs:12,sm:6}
        ],
        editMode:false,
        isModalOpen: false,
        selectedWidgetIndex:null
      }

    }
    handleWidgetSelection=(id) => {

      this.setState({ selectedWidgetId: id })
      const copyWidgets=Object.assign([],this.state.widgets); 
      let widget = Object.assign({}, this.state.widgets[this.state.selectedWidgetIndex]);

      widget.content=this.state.widgetOptions.find(x=>x.name==id).comp;

      copyWidgets[this.state.selectedWidgetIndex]=widget; 
      this.setState({ 
          widgets:copyWidgets
      }) 
      this.onRequestClose();
    }
    deleteEvent=(index)=>{
        const copyWidgets=Object.assign([],this.state.widgets);
        let widget=this.state.widgets[index];
        widget.content="";
        copyWidgets[index]=widget;
        this.setState({
            widgets:copyWidgets
        })
    }
    addEvent=(index)=>{
        this.setState({ 
            isModalOpen: true,
            selectedWidgetIndex:index
        })

    }

    onRequestClose = () => {
        this.setState({
          isModalOpen: false,
        });
      }

    editEvent=()=>{
      this.setState(prevState=>({
        editMode:!prevState.editMode,
      }));
    }
    render() {
      const { classes } = this.props;

      return (

      <div className={classes.root}>
      <AddWidgetDialog handleWidgetSelection={this.handleWidgetSelection} widgets={this.state.widgetOptions} isModalOpen={this.state.isModalOpen} onRequestClose={this.onRequestClose} />
        <div className="gridItem">
        <Grid container spacing={24}>
        <div className="editToggle">
          <input onClick={this.editEvent} type="checkbox" checked data-toggle="toggle" data-on="Ready" data-off="Not Ready" data-onstyle="success" data-offstyle="danger"></input>
        </div>
            {
                this.state.widgets.map((widget,index)=>{
                    return(
                        <Grid item xs={widget.xs} sm={widget.sm}>
                            <Paper className={classes.paper}><Swappable id={widget.id} content={widget.content} delete={this.deleteEvent.bind(this,index)} add={this.addEvent.bind(this,index)} editMode={this.state.editMode}/></Paper>
                         </Grid>
                    )
                })
            }
        </Grid>
        </div>
      </div>
      );
    }
  }

  App.propTypes = {
    classes: PropTypes.object.isRequired
  };

  export default withStyles(styles)(App);
